styles.raw:
--gray: #374151;

.container {
  max-width: 900px;
}

.primo-copy {
  margin: 0 auto;
  width: 100%;
  max-width: 900px; 
  padding-right: 2rem;
  padding-left: 2rem;
  font-size: 1.125rem;
  color: #374151;

  h1 { 
    font-size: 1.875rem;
    font-weight: 500;
    margin-top: 2rem;
  }

  h2 {
    font-size: 1.5rem;
    font-weight: 500;
    margin-top: 1rem;
  }

  ol {
    list-style-type: decimal;
    list-style-position: inside;
  }

  ul {
    list-style-type: disc;
    list-style-position: inside;
  }

  p {
    display: inline-block;
    padding: 0.5rem 0;
  }

  ol {
    list-style-type: decimal;
    list-style-position: inside;
  }

  a {
    color: #1c64f2;
    text-decoration: underline;
  }

  blockquote {
    box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1),
      0 2px 4px -1px rgba(0, 0, 0, 0.06);
    padding: 1.5rem;
  }

  mark {
    --text-opacity: 1;
    color: #161e2e;
    --bg-opacity: 1;
    background-color: #fce96a;
  }
}

@media (min-width: 1024px) {
  .primo-copy h1 {
    font-size: 3rem;
  }

  .primo-copy h2 {
    font-size: 2.25rem;
  }
}

